import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, Button, Alert, Modal, Pressable } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

const Separator = () => (
  <View style={styles.separator} />
);

function Clientes({ navigation }) {
  const [clientes, setClientes] = useState([]);

  useEffect(() => {
    async function fetchMyAPI() {
      const response = await fetch('http://localhost:5101/api/Cliente');
      setClientes(await response.json());
    }
    fetchMyAPI();
  }, []);

  return (
    <View style={{ flex: 1, alignItems: 'center' }}>
      <View style={styles.fixToText}>
        <Button color= '#034C8C'
          title="Clientes"
          onPress={() => navigation.navigate('Clientes')}
        />
        <Button
          title="Aeroportos"
          onPress={() => navigation.navigate('Aeroporto')}
        />
        <Button
          title="Hospedagens"
          onPress={() => navigation.navigate('Hospedagem')}
        />
        <Button
          title="Companhias"
          onPress={() => navigation.navigate('Companhia')}
        />
      </View>
      <View style={styles.fixToList}>
        <Text style={{ width: '10%' }}>Id</Text>
        <Text style={{ width: '30%' }}>Nome</Text>
        <Text style={{ width: '30%' }}>Cpf</Text>
        <Text style={{ width: '30%' }}>Email</Text>
      </View>
      {clientes.map(cli => (
        <View style={styles.fixToList}>
          <Text style={{ width: '10%' }}>{cli.id}</Text>
          <Text style={{ width: '30%' }}>{cli.nome}</Text>
          <Text style={{ width: '30%' }}>{cli.cpf}</Text>
          <Text style={{ width: '30%' }}>{cli.email}</Text>
          <Separator />
        </View>
      ))}

    </View>
  );
};

function Aeroporto({ navigation }) {
  const [aeroportos, setAeroportos] = useState([]);

  useEffect(() => {
    async function fetchMyAPI() {
      const response = await fetch('http://localhost:5101/api/Aeroporto');
      setAeroportos(await response.json());
    }
    fetchMyAPI();
  }, []);
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      {aeroportos.map(aero => (
        <View>
          <Text key={aero.id} style={{ borderWidth: 1, }}>{aero.id}-{aero.nome}-{aero.cnpj}</Text>
        </View>
      ))}

      <Button
        title="Clientes"
        onPress={() => navigation.navigate('Clientes')}
      />
      <Button
        title="Aeroportos"
        onPress={() => navigation.navigate('Aeroporto')}
      />
      <Button
        title="Hospedagens"
        onPress={() => navigation.navigate('Hospedagem')}
      />
      <Button
        title="Companhias"
        onPress={() => navigation.navigate('Companhia')}
      />
    </View>
  );
}

function Hospedagem({ navigation }) {
  const [hospedagens, setHospedagens] = useState([]);

  useEffect(() => {
    async function fetchMyAPI() {
      const response = await fetch('http://localhost:5101/api/Hospedagem');
      setHospedagens(await response.json());
    }
    fetchMyAPI();
  }, []);
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      {hospedagens.map(hos => (
        <View>
          <Text key={hos.id} style={{ borderWidth: 1, }}>{hos.id}-{hos.nome}-{hos.cnpj}</Text>
        </View>
      ))}

      <Button
        title="Clientes"
        onPress={() => navigation.navigate('Clientes')}
      />
      <Button
        title="Aeroportos"
        onPress={() => navigation.navigate('Aeroporto')}
      />
      <Button
        title="Hospedagens"
        onPress={() => navigation.navigate('Hospedagem')}
      />
      <Button
        title="Companhias"
        onPress={() => navigation.navigate('Companhia')}
      />
    </View>
  );
}

function Companhia({ navigation }) {
  const [clientes, setClientes] = useState([]);

  useEffect(() => {
    async function fetchMyAPI() {
      const response = await fetch('http://localhost:5101/api/Companhia');
      setClientes(await response.json());
    }
    fetchMyAPI();
  }, []);
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      {clientes.map(cli => (
        <View>
          <Text key={cli.id} style={{ borderWidth: 1, }}>{cli.id}-{cli.nome}-{cli.cpf}-{cli.email}</Text>
        </View>
      ))}

      <Button
        title="Clientes"
        onPress={() => navigation.navigate('Clientes')}
      />
      <Button
        title="Aeroportos"
        onPress={() => navigation.navigate('Aeroporto')}
      />
      <Button
        title="Hospedagens"
        onPress={() => navigation.navigate('Hospedagem')}
      />
      <Button
        title="Companhias"
        onPress={() => navigation.navigate('Companhia')}
      />
    </View>
  );
}

const Stack = createNativeStackNavigator();


function App() {
  const [modalVisible, setModalVisible] = useState(false);

  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName='Cliente'>
        <Stack.Screen
          name="Clientes" component={Clientes} options={{
            title: 'Clientes',
            headerStyle: { backgroundColor: '#034C8C', }, headerTintColor: '#fff'
          }} />
        <Stack.Screen
          name="Aeroporto"
          component={Aeroporto}
          options={{ title: 'Aeroportos' }}
        />
        <Stack.Screen
          name="Hospedagem"
          component={Hospedagem}
          options={{ title: 'Hospedagens' }}
        />

        <Stack.Screen
          name="Companhia"
          component={Companhia}
          options={{ title: 'Companhias' }}
        />

      </Stack.Navigator>
    </NavigationContainer>
  );
}

export default App;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  titulo1: {
    fontWeight: 'bold',
    fontSize: 50,
    marginBottom: 50,
  },
  separator: {
    marginVertical: 8,
    borderBottomColor: '#737373',
    borderBottomWidth: StyleSheet.hairlineWidth,
  },
  fixToText: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 30,
  },
  fixToList: {
    flexDirection: 'row',
  },
});
