import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, Button, Alert, Modal, Pressable } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

function Clientes({ navigation }) {
  const [clientes, setClientes] = useState([]);

  useEffect(() => {
    async function fetchMyAPI() {
      const response = await fetch('http://localhost:5101/api/Cliente');
      setClientes(await response.json());
    }
    fetchMyAPI();
  }, []);

  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      {clientes.map(cli => (
        <View>
          <Text key={cli.id} style={{ borderWidth: 1, }}>{cli.id}-{cli.nome}-{cli.cpf}-{cli.email}</Text>
        </View>
      ))}

      <Button
        title="Clientes"
        onPress={() => navigation.navigate('Clientes')}
      />
      <Button
        title="Aeroportos"
        onPress={() => navigation.navigate('Aeroporto')}
      />
      <Button
        title="Hospedagens"
        onPress={() => navigation.navigate('Hospedagem')}
      />
      <Button
        title="Companhias"
        onPress={() => navigation.navigate('Companhia')}
      />
    </View>
  );
};

function Aeroporto({ navigation }) {
  const [aeroportos, setAeroportos] = useState([]);

  useEffect(() => {
    async function fetchMyAPI() {
      const response = await fetch('http://localhost:5101/api/Aeroporto');
      setAeroportos(await response.json());
    }
    fetchMyAPI();
  }, []);
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      {aeroportos.map(aero => (
        <View>
          <Text key={aero.id} style={{ borderWidth: 1, }}>{aero.id}-{aero.nome}-{aero.cnpj}</Text>
        </View>
      ))}

      <Button
        title="Clientes"
        onPress={() => navigation.navigate('Clientes')}
      />
      <Button
        title="Aeroportos"
        onPress={() => navigation.navigate('Aeroporto')}
      />
      <Button
        title="Hospedagens"
        onPress={() => navigation.navigate('Hospedagem')}
      />
      <Button
        title="Companhias"
        onPress={() => navigation.navigate('Companhia')}
      />
    </View>
  );
}

function Hospedagem({ navigation }) {
  const [hospedagens, setHospedagens] = useState([]);

  useEffect(() => {
    async function fetchMyAPI() {
      const response = await fetch('http://localhost:5101/api/Hospedagem');
      setHospedagens(await response.json());
    }
    fetchMyAPI();
  }, []);
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      {hospedagens.map(hos => (
        <View>
          <Text key={hos.id} style={{ borderWidth: 1, }}>{hos.id}-{hos.nome}-{hos.cnpj}</Text>
        </View>
      ))}

      <Button
        title="Clientes"
        onPress={() => navigation.navigate('Clientes')}
      />
      <Button
        title="Aeroportos"
        onPress={() => navigation.navigate('Aeroporto')}
      />
      <Button
        title="Hospedagens"
        onPress={() => navigation.navigate('Hospedagem')}
      />
      <Button
        title="Companhias"
        onPress={() => navigation.navigate('Companhia')}
      />
    </View>
  );
}

function Companhia({ navigation }) {
  const [clientes, setClientes] = useState([]);

  useEffect(() => {
    async function fetchMyAPI() {
      const response = await fetch('http://localhost:5101/api/Companhia');
      setClientes(await response.json());
    }
    fetchMyAPI();
  }, []);
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      {clientes.map(cli => (
        <View>
          <Text key={cli.id} style={{ borderWidth: 1, }}>{cli.id}-{cli.nome}-{cli.cpf}-{cli.email}</Text>
        </View>
      ))}

      <Button
        title="Clientes"
        onPress={() => navigation.navigate('Clientes')}
      />
      <Button
        title="Aeroportos"
        onPress={() => navigation.navigate('Aeroporto')}
      />
      <Button
        title="Hospedagens"
        onPress={() => navigation.navigate('Hospedagem')}
      />
      <Button
        title="Companhias"
        onPress={() => navigation.navigate('Companhia')}
      />
    </View>
  );
}

const Stack = createNativeStackNavigator();


function App() {
  const [modalVisible, setModalVisible] = useState(false);

  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName='Cliente'>
        <Stack.Screen
          name="Clientes" component={Clientes} options={{
            title: 'Clientes',
            headerStyle: { backgroundColor: '#034C8C', }, headerTintColor: '#fff',
            headerLeft: () => (
              <View>
                <Modal
                  animationType="slide"
                  transparent={true}
                  visible={modalVisible}
                  onRequestClose={() => {
                    Alert.alert("Modal has been closed.");
                    setModalVisible(!modalVisible);
                  }}
                >
                  <View style={styles.centeredView}>
                    <View style={styles.modalView}>
                      <Text style={styles.modalText}>Menu</Text>
                      <Pressable onPress={() => navigation.navigate('Clientes')}><Text>Clientes</Text></Pressable>
                      <Pressable onPress={() => navigation.navigate('Aeroporto')}><Text>Aeroportos</Text></Pressable>
                      <Pressable onPress={() => navigation.navigate('Hospedagem')}><Text>Hospedagens</Text></Pressable>
                      <Pressable onPress={() => navigation.navigate('Companhia')}><Text>Companhias</Text></Pressable>
                      <Pressable onPress={() => setModalVisible(!modalVisible)}><Text>Fechar</Text></Pressable>
                    </View>
                  </View>
                </Modal>
                <Pressable
                  style={[styles.button]}
                  onPress={() => setModalVisible(true)}
                >
                  <Text>Menu</Text>
                </Pressable>
              </View>),
          }} />
        <Stack.Screen
          name="Aeroporto"
          component={Aeroporto}
          options={{ title: 'Aeroportos' }}
        />
        <Stack.Screen
          name="Hospedagem"
          component={Hospedagem}
          options={{ title: 'Hospedagens' }}
        />

        <Stack.Screen
          name="Companhia"
          component={Companhia}
          options={{ title: 'Companhias' }}
        />

      </Stack.Navigator>
    </NavigationContainer>
  );
}

export default App;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  input: {
    height: 40,
    width: "80%",
    marginBottom: 20,
    borderWidth: 1,
    borderRadius: 10,
    borderColor: "lightgrey",
    padding: 10,
    textAlign: 'center',
  },
  titulo1: {
    fontWeight: 'bold',
    fontSize: 50,
    marginBottom: 50,
  },
  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 22
  },
  modalView: {
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5
  },
  button: {
    borderRadius: 20,
    padding: 10
  },
  buttonOpen: {
    backgroundColor: "#F194FF",
  },
  buttonClose: {
    backgroundColor: "#2196F3",
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center"
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center"
  }
});
