import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, Button, TextInput} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

function Clientes({ navigation }) {
  const [clientes, setClientes] = useState([]);

  useEffect(() => {
    async function fetchMyAPI() {
      const response = await fetch('http://localhost:5101/api/Cliente');
      setClientes(await response.json());
    }
    fetchMyAPI();
  }, []);

  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      {clientes.map(cli => (
        <View>
          <Text key={cli.id} style={{ borderWidth: 1, }}>{cli.id}-{cli.nome}-{cli.cpf}-{cli.email}</Text>
        </View>
      ))}

      <Button
        title="Clientes"
        onPress={() => navigation.navigate('Clientes')}
      />
      <Button
        title="Aeroportos"
        onPress={() => navigation.navigate('Aeroporto')}
      />
      <Button
        title="Hospedagens"
        onPress={() => navigation.navigate('Hospedagem')}
      />
      <Button
        title="Clientes"
        onPress={() => navigation.navigate('Clientes')}
      />
    </View>
  );
};

function Aeroporto({ navigation }) {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Home Screen</Text>
      <Button
        title="Go to Details"
        onPress={() => navigation.navigate('Details')}
      />
      <TextInput
        style={styles.input} />
      <Button
        title="Login"
        onPress={() => navigation.navigate('UselessTextInput')}
      />
    </View>
  );
}

function Hospedagem({ navigation }) {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Details Screen</Text>
      <Button
        title="Go to Details... again"
        onPress={() => navigation.push('Details')}
      />
      <Button title="Go to Home" onPress={() => navigation.navigate('Home')} />
      <Button title="Go back" onPress={() => navigation.goBack()} />
      <Button
        title="Go back to first screen in stack"
        onPress={() => navigation.popToTop()}
      />
    </View>
  );
}

function Companhia({ navigation }) {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Details Screen</Text>
      <Button
        title="Go to Details... again"
        onPress={() => navigation.push('Details')}
      />
      <Button title="Go to Home" onPress={() => navigation.navigate('Home')} />
      <Button title="Go back" onPress={() => navigation.goBack()} />
      <Button
        title="Go back to first screen in stack"
        onPress={() => navigation.popToTop()}
      />
    </View>
  );
}

const Stack = createNativeStackNavigator();


function App() {

  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName='Cliente'>
        <Stack.Screen
          name="Cliente" component={Clientes} options={{
            title: 'Clientes',
            headerStyle: { backgroundColor: '#034C8C', }, headerTintColor: '#fff',
            headerLeft: () => (
              <Button onPress={() => alert('This is a button!')}
                title="Info" color="#fff" />),
          }} />
        <Stack.Screen
          name="Aeroporto"
          component={Aeroporto}
          options={{ title: 'Aeroportos' }}
        />
        <Stack.Screen
          name="Hospedagem"
          component={Hospedagem}
          options={{ title: 'Hospedagens' }}
        />

        <Stack.Screen
          name="Companhia"
          component={Companhia}
          options={{ title: 'Companhias' }}
        />

      </Stack.Navigator>
    </NavigationContainer>
  );
}

export default App;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  input: {
    height: 40,
    width: "80%",
    marginBottom: 20,
    borderWidth: 1,
    borderRadius: 10,
    borderColor: "lightgrey",
    padding: 10,
    textAlign: 'center',
  },
  titulo1: {
    fontWeight: 'bold',
    fontSize: 50,
    marginBottom: 50,
  },
});
